package com.astrium.faceo.client.bean.programming.sps2;import java.io.Serializable;/** * <B>SITE FACEO</B> <BR> *  * <P> * Cette classe est un conteneur d'informations  * </P> * </P> *  * @author  GR * @version 1.0, le 11/06/2010 */public class ErrorBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 8600703599399196547L;	/** error */	private boolean error = false;		/** error Message */	private String message = null;		/** error Code */	private int code = 0;	/**	 * d&eacute;but des m&eacute;thodes	 	 * 	 * Constructeur par d&eacute;faut : vide : convention Bean	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public ErrorBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return boolean : true if error, false else	*/	public boolean getError() {		return this.error ;	}	/** 	 * getter on message	 * 	 * @return String : the error message	*/	public String getMessage() {		return (this.message != null) ? this.message : "";	}	/** 	 * getter on code	 * 	 * @return int : the error code	*/	public int getCode() {		return this.code;	}	/** ----------------------- setters ----------------------- */	/** 	 * getter on error	 * 	 * @param _error (boolean) :  true if error, false else	*/	public void setError(boolean _error) {		this.error = _error;	}	/** 	 * setter on message	 * 	 * @param _message (String) : error message value	*/	public void setMessage(String _message) {		this.message = _message;	}	/** 	 * setter on code	 * 	 * @param _code (int) : error code value	*/	public void setCode(int _code) {		this.code = _code;	}	} // class