package com.astrium.faceo.client.bean.programming.sps2.cancel;import java.io.Serializable;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'Cancel' parameters *   * The Cancel operation allows SPS clients to cancel an accepted task (see clause 6.3.6).  * The service may reject the cancellation for specific reason.  * The response should indicate why the cancellation did not succeed.  * If the cancellation was rejected, the task remains in its current state. *  * </P> * </P> *  * @author  GR * @version 1.0, le 13/07/2010 */public class CancelRequestBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 6108097278155927602L;	/** sensor urn identifier */	private String sensorUrn = null;		/** task identifier */	private String taskId = null;		/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public CancelRequestBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on taskId	 * 	 * @return String : task identifier	*/	public String getTaskId() {		return (this.taskId != null) ? this.taskId : "";	}		/** 	 * getter on sensorUrn	 * 	 * @return String : sensor urn	*/	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on taskId	 * 	 * @param _taskId (String) : task identifier value	*/	public void setTaskId(String _taskId) {		this.taskId = _taskId;	}	/** 	 * setter on sensorUrn	 * 	 * @param _sensorUrn (String) : sensor urn value	*/	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}} // class