package com.astrium.faceo.client.bean.programming.sps2.cancel;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;import com.astrium.faceo.client.bean.programming.sps2.TaskStatusReportBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'Cancel' for one task *   * The Cancel operation allows SPS clients to cancel an accepted task (see clause 6.3.6).  * The service may reject the cancellation for specific reason.  * The response should indicate why the cancellation did not succeed.  * If the cancellation was rejected, the task remains in its current state. *  * </P> * </P> *  * @author  GR * @version 1.0, le 13/07/2010 */public class CancelResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -7018435536298952949L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** Status report */	private TaskStatusReportBean statusReport = new TaskStatusReportBean();		/** Cancel response */	private String response = null;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public CancelResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on statusReport	 * 	 * @return ErrorBean : the Status Report informations	*/	public TaskStatusReportBean getStatusReport() {		return this.statusReport;	}	/** 	 * getter on response	 * 	 * @return String : Cancel response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setErrorMessage(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on statusReport	 * 	 * @param _statusReport (TaskStatusReportBean) : Status Report informations value	*/	public void setStatusReport(TaskStatusReportBean _statusReport) {		this.statusReport = _statusReport;	}		/** 	 * setter on response	 * 	 * @param _response (String) : Cancel response value	*/	public void setResponse(String _response) {		this.response = _response;	}} // class