package com.astrium.faceo.client.bean.programming.sps2.confirm;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;import com.astrium.faceo.client.bean.programming.sps2.TaskStatusReportBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'Confirm' for one task *   * The Confirm operation allows SPS clients to confirm a previously reserved task.  * If accepted i;e; if task has'nt expired yet, the task shall transit from state reserved * to inExecution, otherwise the SPS shall reject the request *  * </P> * </P> *  * @author  GR * @version 1.0, le 24/08/2010 */public class ConfirmResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 8969581130148932002L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** Status report */	private TaskStatusReportBean statusReport = new TaskStatusReportBean();		/** Confirm response */	private String response = null;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public ConfirmResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on statusReport	 * 	 * @return ErrorBean : the Status Report informations	*/	public TaskStatusReportBean getStatusReport() {		return this.statusReport;	}	/** 	 * getter on response	 * 	 * @return String : Cancel response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setErrorMessage(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on statusReport	 * 	 * @param _statusReport (TaskStatusReportBean) : Status Report informations value	*/	public void setStatusReport(TaskStatusReportBean _statusReport) {		this.statusReport = _statusReport;	}		/** 	 * setter on response	 * 	 * @param _response (String) : Confirm response value	*/	public void setResponse(String _response) {		this.response = _response;	}} // class