package com.astrium.faceo.client.bean.programming.sps2.describeResultAccess;import java.io.Serializable;import com.astrium.faceo.client.common.programming.sps2.Sps2GeneralConstants;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'DescribeResultAccess' parameters *   * The DescribeResultAccess operation allows SPS clients to retrieve  * information how to access data that was produced by a specific task,  * or how to retrieve data for a given sensor that is tasked by this SPS in general. * The response may point to a SOS, WMS, WFS any other OGC Web Service  * that provides data any data file or folder on a ftp server any data file  * or file container that is accessible over the Internet Clients provide the ID  * of either a sensor or task to identify what information they are interested in.  *  * </P> * </P> *  * @author  GR * @version 1.0, le 08/07/2010 */public class DescribeResultAccessRequestBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 2286768275892898647L;		/** request type :	 * 1 for task describe result access, 2 for sensor describe result access	 */	private int type = Sps2GeneralConstants.DESCRIBE_ACCESS_SENSOR;	/** sensor urn identifier */	private String sensorUrn = null;		/** task identifier */	private String taskId = null;		/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public DescribeResultAccessRequestBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on type	 * 	 * @return int : type : 1 for task describe result access, 2 for sensor describe result access	*/	public int getType() {		return this.type;	}	/** 	 * getter on taskId	 * 	 * @return String : task identifier	*/	public String getTaskId() {		return (this.taskId != null) ? this.taskId : "";	}		/** 	 * getter on sensorUrn	 * 	 * @return String : sensor urn	*/	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on type	 * 	 * @param _type (int) : type value : 1 for task describe result access, 2 for sensor describe result access	*/	public void setType(int _type) {		this.type = _type;	}	/** 	 * setter on taskId	 * 	 * @param _taskId (String) : task identifier value	*/	public void setTaskId(String _taskId) {		this.taskId = _taskId;	}	/** 	 * setter on sensorUrn	 * 	 * @param _sensorUrn (String) : sensor urn value	*/	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}} // class