package com.astrium.faceo.client.bean.programming.sps2.describeResultAccess;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'DescribeResultAccess' which returns SPS 'DescribeResultAccess' details for one sensor *   * The DescribeResultAccess operation allows SPS clients to retrieve  * information how to access data that was produced by a specific task,  * or how to retrieve data for a given sensor that is tasked by this SPS in general. * The response may point to a SOS, WMS, WFS any other OGC Web Service  * that provides data any data file or folder on a ftp server any data file  * or file container that is accessible over the Internet Clients provide the ID  * of either a sensor or task to identify what information they are interested in.  *  * </P> * </P> *  * @author  GR * @version 1.0, le 08/07/2010 */public class DescribeResultAccessResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -3707497113714111859L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** DescribeTaking response */	private String response = null;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public DescribeResultAccessResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on response	 * 	 * @return String : DescribeResultAccess response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setErrorMessage(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on response	 * 	 * @param _response (String) : DescribeResultAccess response value	*/	public void setResponse(String _response) {		this.response = _response;	}} // class