package com.astrium.faceo.client.bean.programming.sps2.getCapabilities;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'GetCapabilities' which returns SPS 'GetCapabilities' details for one sensor *  * The mandatory GetCapabilities operation allows clients  * to retrieve service metadata from a server.  * The response to a GetCapabilities request shall be service metadata  * about the server, including specific information about the sensors provided by the service,  * supported data encodings and – if supported by the binding employed by the service –  * also metadata about the supported notification functionality. *  * </P> * </P> *  * @author  GR * @version 1.0, le 01/07/2010 */public class GetCapabilitiesResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 6096063586082592147L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** DescribeTaking response */	private String response = null;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public GetCapabilitiesResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on response	 * 	 * @return String : GetCapabilities response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setError(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on response	 * 	 * @param _response (String) : GetCapabilities response value	*/	public void setResponse(String _response) {		this.response = _response;	}} // class