package com.astrium.faceo.client.bean.programming.sps2;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.request.ProgrammingRequestBean;import com.astrium.faceo.client.bean.programming.sps2.responses.TaskingResponseBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'GetTaskResult' which returns SPS task details for one user * </P> * </P> *  * @author  GR * @version 1.0, le 09/06/2010 */public class TaskResultBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 895699652093298859L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** idCartography 	* identifier returned by the applet	* when the segment is added on the cartography	*/	private int idCartography = 0;		/** id */	private String id = null;		/** request status */	private String requestStatus = null;		/** task status */	private String status = null;		/** user */	private String user = null;		/** operation */	private String operation = null;		/** programmingRequestBean */	private ProgrammingRequestBean programmingRequestBean = null;			/** taskingResponseBean */	private TaskingResponseBean taskingResponseBean = null;			/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public TaskResultBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on idCartography	 * 	 * @return int : cartography identifier	*/	public int getIdCartography() {		return this.idCartography;	}	/** 	 * getter on id	 * 	 * @return String : task identifier	*/	public String getId() {		return (this.id != null) ? this.id : "";	}		/** 	 * getter on requestStatus	 * 	 * @return String : request status value	*/	public String getRequestStatus() {		return (this.requestStatus != null) ? this.requestStatus : "";	}		/** 	 * getter on status	 * 	 * @return String : task status value	*/	public String getStatus() {		return (this.status != null) ? this.status : "";	}	/** 	 * getter on user	 * 	 * @return String : user task	*/	public String getUser() {		return (this.user != null) ? this.user : "";	}		/** 	 * getter on operation	 * 	 * @return String : operation ('GetFeasibility', 'Submit', 'Reserve')	*/	public String getOperation() {		return (this.operation != null) ? this.operation : "";	}	/** 	 * getter on programmingRequestBean	 * 	 * @return ProgrammingRequestBean : programming request bean ('GetFeasibility', 'Submit', 'Update')	*/	public ProgrammingRequestBean getProgrammingRequestBean() {		return this.programmingRequestBean;	}	/** 	 * getter on taskingResponseBean	 * 	 * @return TaskingResponseBean : tasking response bean	*/	public TaskingResponseBean getTaskingResponseBean() {		return this.taskingResponseBean;	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setErrorMessage(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on idCartography	 * 	 * @param _idCartography (int) : cartography identifier value	*/	public void setIdCartography(int _idCartography) {		this.idCartography = _idCartography;	}		/** 	 * setter on id	 * 	 * @param _id (String) : task identifier value	*/	public void setId(String _id) {		this.id = _id;	}	/** 	 * setter on requestStatus	 * 	 * @param _requestStatus (String) : request status value	*/	public void setRequestStatus(String _requestStatus) {		this.requestStatus = _requestStatus;	}	/** 	 * setter on status	 * 	 * @param _status (String) : task status value	*/	public void setStatus(String _status) {		this.status = _status;	}	/** 	 * setter on user	 * 	 * @param _user (String) : user task value	*/	public void setUser(String _user) {		user = _user;	}	/** 	 * getter on operation	 * 	 * @param _operation (String) : operation ('GetFeasibility' , 'Submit') value	*/	public void setOperation(String _operation) {		this.operation = _operation;	}		/** 	 * setter on programmingRequestBean	 * 	 * @param _programmingRequestBean (ProgrammingRequestBean) : programming request bean value ('GetFeasibility', 'Submit', 'Update')	*/	public void setProgrammingRequestBean(			ProgrammingRequestBean _programmingRequestBean) {		this.programmingRequestBean = _programmingRequestBean;	}	/** 	 * setter on taskingResponseBean	 * 	 * @param _taskingResponseBean (taskingResponseBean) : tasking response bean value	*/	public void setTaskingResponseBean(			TaskingResponseBean _taskingResponseBean) {		this.taskingResponseBean = _taskingResponseBean;	}} // class