package com.astrium.faceo.client.bean.programming.sps2.describeSensor;import java.io.Serializable;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'DescribeSensor' parameters *   * The DescribeSensor operation allows a client to request a detailed description  * of a sensor. The request can be targeted at a description that was valid  * at a certain point in or during a certain period of time in the past.  *  * </P> * </P> *  * @author  GR * @version 1.0, le 19/11/2010 */public class DescribeSensorRequestBean implements Serializable {		/**	 * 	 */	private static final long serialVersionUID = -3347344590657959928L;	/** sensor urn identifier */	private String sensorUrn = null;		/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public DescribeSensorRequestBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on sensorUrn	 * 	 * @return String : sensor urn	*/	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on sensorUrn	 * 	 * @param _sensorUrn (String) : sensor urn value	*/	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}} // class