package com.astrium.faceo.client.bean.programming.sps2.getCapabilities;import java.io.Serializable;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'GetCapabilities' parameters *  * The mandatory GetCapabilities operation allows clients  * to retrieve service metadata from a server.  * The response to a GetCapabilities request shall be service metadata  * about the server, including specific information about the sensors provided by the service,  * supported data encodings and – if supported by the binding employed by the service –  * also metadata about the supported notification functionality. *  * </P> * </P> *  * @author  GR * @version 1.0, le 01/07/2010 */public class GetCapabilitiesRequestBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -8407081281290723207L;		/** sensor urn identifier */	private String sensorUrn = null;		/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public GetCapabilitiesRequestBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on sensorUrn	 * 	 * @return String : sensor urn identifier	*/	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on sensorUrn	 * 	 * @param _sensorUrn (String) : sensor urn identifier value	*/	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}} // class