package com.astrium.faceo.client.bean.programming.sps2.getSensorAvailability;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.request.SurveyPeriodBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'GetSensorAvailability' * request *  * An EO system may not be available over a period of time for different reasons * such as workload, maintenance, etc. The GetSensorAvailability operation * allows the client to obtain a preview of the periods of availability of a * sensor before a feasibility study is requested. *  * The granularity of the provided information is up to the data provider who * can choose to describe its exact workload or simply list approximate periods * of availability. For instance, a provider may wish to list a period of one * week as soon as the sensor is available for tasking at least during 50% of * the period. This allows a provider to choose the most appropriate granularity * of information to help the users while maintaining its exact workload secret. *  * </P> * </P> *  * @author GR * @version 1.0, le 03/11/2010 */public class GetSensorAvailabilityRequestBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 3012886482636300715L;	/** sensor urn identifier */	private String sensorUrn = null;	/** 	 * Period during which the client asks for sensor availability. 	 */	private SurveyPeriodBean requestedPeriod = new SurveyPeriodBean();	/**	 * methods starting	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration is	 * required for a serializable class. (GWT)	 */	public GetSensorAvailabilityRequestBean() {	}	/** ----------------------- getters ----------------------- */	/**	 * getter on sensorUrn	 * 	 * @return String : sensor urn identifier	 */	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/**	 * getter on requestedPeriod	 * 	 * @return String : requested period	 */	public SurveyPeriodBean getRequestedPeriod() {		return this.requestedPeriod;	}	/** ----------------------- setters ----------------------- */	/**	 * setter on sensorUrn	 * 	 * @param _sensorUrn	 *            (String) : sensor urn identifier value	 */	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}	/**	 * setter on requestedPeriod	 * 	 * @param _requestedPeriod	 *            (SurveyPeriodBean) : requested Period value	 */	public void setRequestedPeriod(SurveyPeriodBean _requestedPeriod) {		this.requestedPeriod = _requestedPeriod;	}} // class