package com.astrium.faceo.client.bean.programming.sps2.getSensorAvailability;import java.io.Serializable;import java.util.HashMap;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;import com.astrium.faceo.client.bean.programming.sps2.request.SurveyPeriodBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'GetSensorAvailability' * response *  * Some missions can provide many details about sensor availability while some others cannot,  * so that there are no special requirements on the granularity and the accuracy  * of the availability periods returned.  * The service is free to choose the level of details of the response.  * For example, one global period or several short periods of availability can be returned  * and either precise time range with hours or date only can be used. *  * For the same reason, the server may not trim availability periods to the exact period  * of time given by the client in the request since that would allow for precise discovery  * of the systemâ€™s workload.  * Typically, when the requested period is small, a larger period than the one requested  * can be included in the response in order to prevent clients from discovering more precise  * information by issuing multiple well-chosen requests. *  * The responsePeriod also indicates the period for which availability  * was really assessed. For instance, when the requested period is large  * (i.e. more than one year in the future), the ground segment may not be available  * to provide information on the system availability for the whole period. *  * Instead, the server can describe its availability only up to its maximum planning horizon  * and indicate this with the responsePeriod attribute in the response. *  * </P> * </P> *  * @author GR * @version 1.0, le 03/11/2010 */public class GetSensorAvailabilityResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -4963630907006509289L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** GetSensorAvailability response */	private String response = null;	/** sensor urn identifier */	private String sensorUrn = null;	/** 	 * Period for which availability has been assessed by the server. 	 * It can be different from the requested period as explained below. 	 */	private SurveyPeriodBean responsePeriod = new SurveyPeriodBean();	/** 	 * Period(s) of availability of the sensor. 	 * When no periods are listed, the sensor is not available 	 * at all during the response period. 	 */	private HashMap<String, SurveyPeriodBean> availabilityPeriod = 		new HashMap<String, SurveyPeriodBean>();	/**	 * methods starting	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration is	 * required for a serializable class. (GWT)	 */	public GetSensorAvailabilityResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on response	 * 	 * @return String : DescribeTasking response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}		/** 	 * getter on sensorUrn	 * 	 * @return String : sensor urn	*/	public String getSensorUrn() {		return (this.sensorUrn != null) ? this.sensorUrn : "";	}	/**	 * getter on responsePeriod	 * 	 * @return String : response period	 */	public SurveyPeriodBean getResponsePeriod() {		return this.responsePeriod;	}	/**	 * getter on availabilityPeriod	 * 	 * @return HashMap<String, SurveyPeriodBean> : Map of availability period	 */	public HashMap<String, SurveyPeriodBean> getAvailabilityPeriod() {		return this.availabilityPeriod;	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setError(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on response	 * 	 * @param _response (String) : DescribeTasking response value	*/	public void setResponse(String _response) {		this.response = _response;	}	/** 	 * setter on sensorUrn	 * 	 * @param _sensorUrn (String) : sensor urn value	*/	public void setSensorUrn(String _sensorUrn) {		this.sensorUrn = _sensorUrn;	}	/**	 * setter on responsePeriod	 * 	 * @param _responsePeriod	 *            (SurveyPeriodBean) : response Period value	 */	public void setResponsePeriod(SurveyPeriodBean _responsePeriod) {		this.responsePeriod = _responsePeriod;	}	/**	 * setter on availabilityPeriod	 * 	 * @param _availabilityPeriod	 *            (HashMap<String, SurveyPeriodBean>) : Map of availability Period value	 */	public void setAvailabilityPeriod(			HashMap<String, SurveyPeriodBean> _availabilityPeriod) {		this.availabilityPeriod = _availabilityPeriod;	}} // class