package com.astrium.faceo.client.bean.programming.sps2.getStatus;import java.io.Serializable;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;import com.astrium.faceo.client.bean.programming.sps2.TaskStatusReportBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the operation's results  * 'GetStatus' which returns SPS task GetStatus details for one task. *  * The GetStatus operation allows SPS clients to retrieve status reports  * about a tasking request or a task.  * This operation is the default mechanism to retrieve status information  * about a task or tasking request. * </P> * </P> *  * @author  GR * @version 1.0, le 17/06/2010 */public class GetStatusResponseBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = -4346041518429958062L;	/** error Message */	private ErrorBean error = new ErrorBean();		/** Status report */	private TaskStatusReportBean statusReport = new TaskStatusReportBean();	/** task User */	private String user = null;		/** GetStatus response */	private String response = null;	/** update task : true if task has been modified, false else */	private boolean updateTask = false;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public GetStatusResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}	/** 	 * getter on statusReport	 * 	 * @return ErrorBean : the Status Report informations	*/	public TaskStatusReportBean getStatusReport() {		return this.statusReport;	}	/** 	 * getter on user	 * 	 * @return String : task user	*/	public String getUser() {		return (this.user != null) ? this.user : "";	}	/** 	 * getter on response	 * 	 * @return String : GetStatus response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** 	 * getter on updateTask	 * 	 * @return String : true if task has been modified, false else	*/	public boolean getUpdateTask() {		return this.updateTask;	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setError(ErrorBean _error) {		this.error = _error;	}		/** 	 * setter on statusReport	 * 	 * @param _statusReport (TaskStatusReportBean) : Status Report informations value	*/	public void setStatusReport(TaskStatusReportBean _statusReport) {		this.statusReport = _statusReport;	}	/** 	 * setter on user	 * 	 * @param _user (String) : task user value	*/	public void setUser(String _user) {		this.user = _user;	}		/** 	 * setter on response	 * 	 * @param _response (String) : GetStatus response value	*/	public void setResponse(String _response) {		this.response = _response;	}		/** 	 * setter on updateTask	 * 	 * @param _updateTask (boolean) : true if task has been modified, false else	*/	public void setUpdateTask(boolean _updateTask) {		this.updateTask = _updateTask;	}} // class