ersionUID = 7081129922953570894L;	// ---------------- Survey Period ---------------------	/**	 * The SurveyPeriod is a time range specifying the period during which the feasibility or     * tasking is done.	 */		/** start period */	private String strStartDate = null;		private Date startDate = null;	/** end period */	private String strEndDate = null;		private Date endDate = null;	/**	 * debut des methodes	 	 * 	 * Constructeur par defaut : vide	 */	public SurveyPeriodBean() {	}	/** getters */		// ---------------- Survey Period ---------------------	/** 	 * getter on startDate	 * 	 * @return String : start date	*/	public String getStrStartDate() {		return (this.strStartDate != null) ? this.strStartDate : "";	}	/** 	 * getter on startDate	 * 	 * @return Date : start date	*/	public Date getStartDate() {		return this.startDate;	}	/** 	 * getter on strEndDate	 * 	 * @return String : end date	*/	public String getStrEndDate() {		return (this.strEndDate != null) ? this.strEndDate : "";	}	/** 	 * getter on endDate	 * 	 * @return Date : end date	*/	public Date getEndDate() {		return this.endDate;	}	/** setters */	// ---------------- Survey Period ---------------------	/** 	 * setter on strStartDate	 * 	 * @param _strStartDate (String): start Date value	*/	public void setStrStartDate(String _strStartDate) {		strStartDate = _strStartDate;	}	/** 	 * setter on startDate	 * 	 * @param _startDate (Date): start Date value	*/	public void setStartDate(Date _startDate) {		this.startDate = _startDate;	}	/** 	 * setter on strEndDate	 * 	 * @param _strEndDate (String): end Date value	*/	public void setStrEndDate(String _strEndDate) {		this.strEndDate = _strEndDate;	}	/** 	 * setter on endDate	 * 	 * @param _endDate (Date): end Date value	*/	public void setEndDate(Date _endDate) {		this.endDate = _endDate;	}} // class