package com.astrium.faceo.client.bean.programming.sps2.request;/* * @(#)TimeOfInterestBean.java	 1.0  20/04/2010 * * * PROJET       : SITE FACEO * * LANGUAGE     : Java * * DESCRIPTION  : Cette classe est un conteneur d'informations pour les requ&ecirc;tes  * des messages XML de l'op&eacute;ration 'GetFeasibility' du standard OGC SPS 2.0. *  * Survey Period or Temporal series *  * Only Survey Period in this implementation * * CREATION : * --------------------------------------------------------------------- * | Date       | Version | Description                                | * --------------------------------------------------------------------- * | 20/04/2010 |    1.0  |                                            | * --------------------------------------------------------------------- * * MODIFICATION : * --------------------------------------------------------------------- * | Date       | Version | Description                                | * --------------------------------------------------------------------- * |            |         |                                            | * --------------------------------------------------------------------- * *//* * sample of GetFeasibility Request *  * sample of SurveyPeriod for GetFeasibility Request *  *  <eo:TimeOfInterest>        <eo:SurveyPeriod>             <eo:min>2010-05-20T00:00:00Z</eo:min>             <eo:max>2010-05-30T00:00:00Z</eo:max>        </eo:SurveyPeriod>    </eo:TimeOfInterest> */// importimport java.io.Serializable;/** * <B>SITE FACEO</B> <BR> *  * <P> * Cette classe est un conteneur d'informations pour l'op&eacute;ration SPS 'GetFeasibility' * </P> *  Survey Period or Temporal series *   * Only Survey Period in this implementation * </P><BR> *  * sample of SurveyPeriod for GetFeasibility Request *  *  <eo:TimeOfInterest> *      <eo:SurveyPeriod> *           <eo:min>2010-05-20T00:00:00Z</eo:min> *           <eo:max>2010-05-30T00:00:00Z</eo:max> *      </eo:SurveyPeriod> *  </eo:TimeOfInterest> *  * @author  GR * @version 1.0, le 20/04/2010 */public class TimeOfInterestBean implements Serializable {	/**	 * 	 */	private static final long serialVersionUID = 6337391917225411992L;		/** Time Of Interest Type */	private int type = 1; // 1 for Survey Period, 2 for Temporal series	// ---------------- Survey Period ---------------------	/**	 * The SurveyPeriod is a time range specifying the period during which the feasibility or     * tasking is done.	 */		/** survey Period */	private SurveyPeriodBean surveyPeriod = new SurveyPeriodBean();	// ---------------- Temporal series ---------------------	/**	 * It be used to specify a temporal series of acquisitions. 	 * A temporal series contains a SurveyPeriod element and parameters that define 	 * the number of occurrences, the periodicity and the latest start date of the series.	 */	/** temporal series */	private TemporalSeriesBean temporalSeries = new TemporalSeriesBean();	/**	 * debut des methodes	 	 * 	 * Constructeur par defaut : vide	 */	public TimeOfInterestBean() {	}	/** getters */		// ---------------- Survey Period ---------------------	/** 	 * getter on type	 * 	 * @return int : type // 1 for Survey Period, 2 for Temporal series	*/	public int getType() {		return this.type;	}	/** 	 * getter on surveyPeriod	 * 	 * @return SurveyPeriodBean : survey Period	*/	public SurveyPeriodBean getSurveyPeriod() {		return this.surveyPeriod;	}	// ---------------- Temporal series ---------------------		/** 	 * getter on temporalSeries	 * 	 * @return TemporalSeriesBean : temporal series	*/	public TemporalSeriesBean getTemporalSeries() {		return this.temporalSeries;	}	/** setters */	/** 	 * setter on type	 * 	 * @param _type (int): type value : 1 for Survey Period, 2 for Temporal series	*/	public void setType(int _type) {		this.type = _type;	}	// ---------------- Survey Period ---------------------	/** 	 * setter on surveyPeriod	 * 	 * @param _surveyPeriod (SurveyPeriodBean): survey Period value	*/	public void setSurveyPeriod(SurveyPeriodBean _surveyPeriod) {		this.surveyPeriod = _surveyPeriod;	}	// ---------------- Temporal series ---------------------	/** 	 * setter on temporalSeries	 * 	 * @param _temporalSeries (TemporalSeriesBean): temporal Series value	*/	public void setTemporalSeries(TemporalSeriesBean _temporalSeries) {		this.temporalSeries = _temporalSeries;	}} // class