package com.astrium.faceo.client.bean.programming.sps2.validate;import java.io.Serializable;import java.util.HashMap;import java.util.Map;import com.astrium.faceo.client.bean.programming.sps2.ErrorBean;import com.astrium.faceo.client.bean.programming.sps2.TaskStatusReportBean;/** * <B>FACEO HMAFO</B> <BR> *  * <P> * This class is a informations container for the 'Validate' parameters *   * Several acquisition attempts are sometimes necessary to obtain a satisfying result  * (case of optical satellites on zones with cloudy tendency for example).  * The Validate operation can be used by the customer to indicate to the server  * that an acquisition is satisfactory and thus to stop collecting new images for this area. *  * </P> * </P> *  * @author  GR * @version 1.0, le 10/12/2010 */public class ValidateResponseBean implements Serializable {		/**	 * 	 */	private static final long serialVersionUID = 7785195757789277709L;	/** error Message */	private ErrorBean error = new ErrorBean();	/** task identifier */	private String taskId = null;		/** Map of segments identifiers */	private Map<Integer, String> segmentsId = new HashMap<Integer, String>();		/** Status report */	private TaskStatusReportBean statusReport = new TaskStatusReportBean();	/** Cancel response */	private String response = null;	/**	 * methods starting	 	 * 	 * default constructor : empty : Bean convention	 */	/**	 * Default Constructor. The Default Constructor's explicit declaration	 * is required for a serializable class. (GWT)	*/	public ValidateResponseBean() {	}	/** ----------------------- getters ----------------------- */		/** 	 * getter on error	 * 	 * @return ErrorBean : the error message	*/	public ErrorBean getError() {		return this.error;	}		/** 	 * getter on taskId	 * 	 * @return String : task identifier	*/	public String getTaskId() {		return (this.taskId != null) ? this.taskId : "";	}		/** 	 * getter on segmentsId	 * 	 * @return Map : segments identifiers	*/		public Map<Integer, String> getSegmentsId() {		return this.segmentsId;	}	/** 	 * getter on statusReport	 * 	 * @return ErrorBean : the Status Report informations	*/	public TaskStatusReportBean getStatusReport() {		return this.statusReport;	}	/** 	 * getter on response	 * 	 * @return String : Cancel response	*/	public String getResponse() {		return (this.response != null) ? this.response : "";	}	/** ----------------------- setters ----------------------- */	/** 	 * setter on error	 * 	 * @param _error (ErrorBean) : error message value	*/	public void setError(ErrorBean _error) {		this.error = _error;	}	/** 	 * setter on taskId	 * 	 * @param _taskId (String) : task identifier value	*/	public void setTaskId(String _taskId) {		this.taskId = _taskId;	}	/** 	 * setter on segmentsId	 * 	 * @param _segmentsId (Map) : segments identifiers values	*/	public void setSegmentsId(Map<Integer, String> _segmentsId) {		this.segmentsId = _segmentsId;	}		/** 	 * setter on statusReport	 * 	 * @param _statusReport (TaskStatusReportBean) : Status Report informations value	*/	public void setStatusReport(TaskStatusReportBean _statusReport) {		this.statusReport = _statusReport;	}		/** 	 * setter on response	 * 	 * @param _response (String) : Cancel response value	*/	public void setResponse(String _response) {		this.response = _response;	}} // class